  });
}
//class direacet className
function App() {
  // deklarasi variable
  const [list, setList] = React.useState(
    JSON.parse(window.localStorage.getItem("LOCAL_LIST")) || [] //if true and Authorization
  );

  const [inputState, setInputState] = React.useState({
    key: "",
    name: "",
  });

  React.useEffect(function() {
    window.localStorage.setItem(
      "LOCAL_LIST",
      JSON.stringify(list)
    );
  },[list]);

  // console.log(inputState)
  return (
    <div className="App">
      <img src={logo} className="App-logo" alt="logo" />
      <div className="wrapper">  
            <h1>Form</h1>
            <div>
                <div className="form-input">
                    <input 
                    type="text" 
                    id="input"
                    value={inputState.name}
                    onChange={(event) => setInputState(event.target.value)}  placeholder="Please Insert Text" />
                </div>
                <div className="form-input">
                    <button type="button" id="button" 
                    onClick={function(){
                      //nambah array dari array yang udah ada
                      setList([...list, {key: new Date(), name:inputState}])
                    }}
                     className="button-class">Submit</button>
                </div>

            </div>
            <center>
            <div id="result">
              <div>Results: </div>
              {/* wajib ada key setiap return */}

              {list.map((value, index) =>{
                return <Card 
                key={index}
                name={value.name}
                onRemove={function(){
                  setList([...list.filter((d, i) => d.key !== value.key)])
                }}
                />;
              })}
            </div>
            <h1> Profile </h1>
            <Card name="test" onRemove={function(){}} />
            </center>
        </div>
    </div>
  );
}

export default App;
