import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [list, setList] = React.useState([
    {key : '1' ,name: 'Caesar'},
    {key : '2' ,name: 'David'}

  ]);
  const [inputState, setInputState] = React.useState(null);
  console.log(inputState);
  return (
    <div className="App">
      <img src={logo} className="App-logo" alt="logo" />
      <div className="wrapper">
        <h1>FORM</h1>
        <div className="form-input">
            <input type="text" 
            onChange={(event) => setInputState({
              key: new Date().toISOString,
              name: event.target.name
            }

            )} id="input" placeholder="please input username"/>
            <button type="button" 
            onClick={() => {
              setList([...list, inputState]);
            }} 
            id="button">
              submit
            </button>
        </div>
        <div id="result">Result:
          {list.map((value, index) => {
              return (
                <div 
                key ={index}
                style={{
                  color: "blue",
                  border: "1px solid grey",
                  display: "flex",
                  justifyContent: "space-between",

                  }}>
                  <div>{value.name}</div>
                  <button onClick={()=>{
                    setList([...list.filter( (d, i) => d.key !== value.key)]);
                  }}> remove</button>
                </div>
              );
          })}
        </div>
    </div>
    </div>
  );
}

export default App;
