import React from 'react';
import logo from './logo.svg';
import './App.css';

//komponen yang ada dibrowser ada disini

//class direacet className
function App() {
  // deklarasi variable
  const [list, setList] = React.useState(
    JSON.parse(window.localStorage.getItem("LOCAL_LIST") || [] ) //if true and 
  );

  const [inputState, setInputState] = React.useState({
    key: "",
    name: "",
  });
  ;
  console.log(window.localStorage.getItem("LOCAL_LIST"));
  window.localStorage.setItem(
    "LOCAL_LIST",
    JSON.stringify({key: "1", name: "test 1"})
    );

  window.localStorage.removeItem("LOCAL_LIST");
  // console.log(inputState)
  return (
    <div className="App">
      <img src={logo} className="App-logo" alt="logo" />
      <div className="wrapper">  
            <h1>Form</h1>
            <div>
                <div className="form-input">
                    <input type="text" onChange={(event) => setInputState(event.target.value)} id="input" placeholder="input your username" />
                </div>
                <div className="form-input">
                    <button type="button" id="button" 
                    onClick={()=> {
                      //nambah array dari array yang udah ada
                      setList([...list, {key: new Date(), name:inputState}])
                    }}
                     className="button-class">Submit</button>
                </div>

            </div>
            <center>
            <div id="result">
              <div>Results: </div>
              {/* wajib ada key setiap return */}

              {list.map((value, index) =>{
                var a = new Date().toISOString
                console.log(a)
                return <div key={index} 
                style={{
                  color:"red", 
                  padding:10, 
                  border: "1px solid grey",
                  marginBottom:10,
                  display: "flex",
                  justifyContent: "space-between",
                  width:"50%",
                  borderRadius:"20px",
                  }}>
                  <div>{value.name}</div>
                  <button onClick={() => {
                    setList([...list.filter((d,i) => d.key !== value.key)]);
                    setInputState({key:"", name:""});
                  }} 
                  className="button-remove">
                    remove
                    </button>
                  </div>

              })}
            </div>
            </center>
        </div>
    </div>
  );
}

export default App;
