import React from 'react';
import logo from './logo.svg';
import './App.css';

//komponen yang ada dibrowser ada disini

//class direacet className
function App() {
  // deklarasi variable
  const [list, setList] = React.useState([
    {key: "1", name: "test 1"},
    {key: "2", name: "test 2"},
    {key: "13", name: "test 3"},

  ]);
  const [inputState, setInputState] = React.useState({
    key: "",
    name: "",
  });
  // console.log(inputState)
  return (
    <div className="App">
      <div className="wrapper">  
            <h1>Form</h1>
            <div>
                <div className="form-input">
                    <input type="text" onChange={(event) => setInputState({
                      key: new Date().toISOString,
                      name: event.target.value
                    })} id="input" placeholder="input your username" />
                </div>
                <div className="form-input">
                    <button type="button" id="button" 
                    onClick={()=> {
                      //nambah array dari array yang udah ada
                      setList([...list, inputState]);
                    }}
                    >Submit</button>
                </div>

            </div>
            <div id="result">
              <div>Results: </div>
              {/* wajib ada key setiap return */}

              {list.map((value, index) =>{
                return <div key={index} 
                style={{
                  color:"red", 
                  padding:10, 
                  border: "1px solid grey",
                  marginBottom:10,
                  display: "flex",
                  justifyContent: "space-between"
                  }}>
                  <div>{value.name}</div>
                  <button onClick={() => {
                    setList([...list.filter((d,i) => d.key !== value.key)]);
                    setInputState({key:"", name:""});
                  }} 
                  className="button-remove">
                    remove
                    </button>
                  </div>

              })}
            </div>
        </div>
    </div>
  );
}

export default App;
